import React from 'react';
import { NextPageContext, NextPage } from 'next';

import styled from 'styled-components';
import { EpubBook } from '../interfaces/books';

const Container = styled.div`
  color: blue
`;

interface Props {
  books: EpubBook[];
}

const Home: NextPage<Props> = ({ books }) => {
  console.log('AAAA', books);

  return (
    <Container>
      HOME
    </Container>
  );
};
// eslint-disable-next-line @typescript-eslint/unbound-method
Home.getInitialProps = (context: NextPageContext<any>): any => {
  const { req } = context;
  if (req) {
    const fs = require('fs');
    const { EpubParser } = require('@ridi/epub-parser');
    const books: EpubBook[] = [];

    fs.readdir('public', async (err, files) => {
      // eslint-disable-next-line no-restricted-syntax
      for (const file of files) {
        if (file.includes('.epub')) {
          const fileName = file.split('.epub')[0];
          const parser = new EpubParser(file);
          // eslint-disable-next-line no-await-in-loop
          const book: EpubBook = await parser.parse({
            validatePackage: true,
            parseStyle: false,
            unzipPath: `public/epub/${fileName}`,
          });

          books.push(book);
        }
      }
    });

    return {
      books,
    };
  }
  return {};
};

export default Home;
