/* eslint-disable react/no-danger */
import React, {
  useEffect, useState, useRef, useCallback,
} from 'react';

import styled from 'styled-components';

import { EpubSpineItem } from '../../interfaces/books';

const ViewrPage = styled.section`
  min-height: 100vh;
  padding-bottom: 0;
  overflow: auto;
`;

const ViewerContents = styled.div`
  box-sizing: border-box;
  height: 1008px;
  overflow: hidden;
`;

const ViewSection = styled.section`
  height: 100%;
  column-fill: auto;
  column-gap: 46px;
  column-width: ${(props) => props.styleProps.width}px;
`;


const ViewArticle = styled.article`
  box-sizing: border-box;
  visibility: visible;
  width: ${(props) => props.styleProps.width}px;
  height: ${(props) => props.styleProps.height}px;
  vertical-align: top;
  white-space: initial;
  display: inline-block;
  font-size: 1em !important;
  line-height: 1.67em !important;
  font-family: kopup_dotum !important;
  margin: 50px 139px;
  overflow: hidden;
  // & ${ViewSection} {
  //   width: 1979px;
  // }
`;

const Contents = styled.div`
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
`;

interface Props {
  spines: EpubSpineItem[];
  viewerSpines: string[];
}

const Viewer: React.FunctionComponent<Props> = ({ spines, viewerSpines }) => {
  const [viewerWidth, setViewerWidth] = useState(0);
  const [viewerHeight, setViewerHeight] = useState(0);

  const contentRef = useRef(null);
  const viewSectionRef = useRef(null);

  useEffect(() => {
    setViewerWidth(window.innerWidth * 0.7);
    setViewerHeight(window.innerHeight * 0.9);
  }, []);

  const clickRight = useCallback(() => {
    if (viewSectionRef && contentRef) {
      const { current: contentRefCurrent } = contentRef;
      const { current: viewSectionRefCurrent } = viewSectionRef;
      console.log('AAAA', contentRefCurrent.style.columnCount);
      console.log('BBBB', viewSectionRefCurrent.style.columnCount);
      console.log('ccc', document.body.clientWidth);
      window.scrollTo(600, 0);
    }
  }, [viewSectionRef, contentRef]);

  return (
    <ViewrPage>
      <ViewerContents>
        <ViewArticle
          onClick={clickRight}
          styleProps={{
            width: viewerWidth,
            height: viewerHeight,
          }}
        >
          <ViewSection
            ref={viewSectionRef}
            styleProps={{
              width: viewerWidth,
              height: viewerHeight,
            }}
          >
            <Contents ref={contentRef} dangerouslySetInnerHTML={{ __html: viewerSpines[1] }} />
          </ViewSection>
        </ViewArticle>
      </ViewerContents>
    </ViewrPage>


  );
};

export default Viewer;
