/* eslint-disable react/no-danger */
import React, {
  useState, useRef, useCallback, useEffect,
} from 'react';
import { useDispatch } from 'react-redux';

import styled from 'styled-components';

import {
  ViewerButton,
  ViewerArticle,
  ViewerSection,
  ViewerContents,
} from '../../styles/viewer';

import * as viewerActions from '../../reducers/viewer';

import { VIEWER_PAGE_GAP } from '../../constants/viewer';

import { ViewerStyle } from '../../interfaces/viewer';

const Article = styled(ViewerArticle)`
  overflow: hidden;
`;

const RightButton = styled(ViewerButton)`
  right: 2em;
`;

const LeftButton = styled(ViewerButton)`
  left: 2em;
`;

interface Props {
  viewerWidth: number;
  viewerHeight: number;
  pageColumnOffset: number;
  viewerSpine: string;
  isFirstPage: boolean;
  isLastPage: boolean;
  viewerStyle: ViewerStyle;
}

const ViewerPage: React.FunctionComponent<Props> = ({
  viewerWidth, viewerHeight,
  pageColumnOffset,
  viewerSpine,
  isFirstPage, isLastPage,
  viewerStyle,
}) => {
  const { widthRatio } = viewerStyle;

  const dispatch = useDispatch();

  const [newViewerWidth, setNewViewerWidth] = useState(viewerWidth);
  const [nowViewerCount, setNowViewerCount] = useState(0);

  const viewArticleRef = useRef(null);

  useEffect(() => {
    if (widthRatio) {
      setNewViewerWidth(Math.floor(Number(viewerWidth) * (Number(widthRatio) / 100)));
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [widthRatio]);

  useEffect(() => {
    const { current: viewArticleRefCurrent } = viewArticleRef;
    viewArticleRefCurrent.scrollLeft = pageColumnOffset * (newViewerWidth + VIEWER_PAGE_GAP);
    setNowViewerCount(pageColumnOffset);
  }, [viewerSpine, pageColumnOffset, newViewerWidth]);

  const clickRight = useCallback(() => {
    const { current: viewArticleRefCurrent } = viewArticleRef;

    setNowViewerCount(nowViewerCount + 1);
    viewArticleRefCurrent.scrollLeft += (newViewerWidth + VIEWER_PAGE_GAP);
    dispatch(viewerActions.setCountUpViewerPageCount());
  }, [dispatch, nowViewerCount, newViewerWidth]);

  const clickLeft = useCallback(() => {
    const { current: viewArticleRefCurrent } = viewArticleRef;
    setNowViewerCount(nowViewerCount - 1);
    viewArticleRefCurrent.scrollLeft -= (newViewerWidth + VIEWER_PAGE_GAP);
    dispatch(viewerActions.setCountDownViewerPageCount());
  }, [dispatch, nowViewerCount, newViewerWidth]);

  return (
    <>
      <Article
        ref={viewArticleRef}
        onClick={clickRight}
        styleProps={{
          ...viewerStyle,
          width: newViewerWidth,
          height: viewerHeight,
        }}
      >
        <ViewerSection
          styleProps={{
            ...viewerStyle,
            width: newViewerWidth,
            height: viewerHeight,
          }}
        >
          <ViewerContents dangerouslySetInnerHTML={{ __html: viewerSpine }} />
        </ViewerSection>
      </Article>
      {!isFirstPage && <LeftButton onClick={clickLeft}>Left</LeftButton>}
      {!isLastPage && <RightButton onClick={clickRight}>Right</RightButton>}
    </>
  );
};

export default ViewerPage;
