import React, { useRef, useEffect, useCallback } from 'react';
import { useDispatch } from 'react-redux';

import styled from 'styled-components';

import ViewerCount from './ViewerCount';

import {
  ViewerArticle,
  ViewerSection,
  ViewerContents,
} from '../../styles/viewer';

import * as viewerActions from '../../reducers/viewer';

import { VIEWER_PAGE_GAP } from '../../constants/viewer';

import { EpubSpineItem } from '../../interfaces/books';
import { ViewerStyle } from '../../interfaces/viewer';

const HiddenArticle = styled(ViewerArticle)`
  overflow: scroll;
`;

const HiddenSection = styled(ViewerSection)`
  visibility: hidden;
`;

interface Props {
  viewerWidth: number;
  viewerHeight: number;
  viewers: string[];
  spines: EpubSpineItem[];
  viewerStyle: ViewerStyle;
}

const ViewerCalculator: React.FunctionComponent<Props> = ({
  viewerWidth, viewerHeight,
  viewers, spines,
  viewerStyle,
}) => {

  const setCountCallback = useCallback((count: number, index: number) => {
    console.log('Index', index);
    console.log('Count', count);
  }, []);

  return (
    viewers.map((viewer, index) => (
      <ViewerCount
        viewerWidth={viewerWidth}
        viewerHeight={viewerHeight}
        viewer={viewer}
        viewerStyle={viewerStyle}
        setCountCallback={(count) => setCountCallback(count, index)}
      />
    ))
  );
};

export default ViewerCalculator;
