import React, { useEffect, useState } from 'react';

import styled from 'styled-components';

import { EpubSpineItem } from '../../interfaces/books';

const ViewArticle = styled.article`
  box-sizing: border-box;
  visibility: visible;
  width: 629px;
  height: 908px;
  vertical-align: top;
  white-space: initial;
  display: inline-block;
  font-size: 1em !important;
  line-height: 1.67em !important;
  font-family: kopup_dotum !important;
  margin: 50px 139px;
`;

const ViewSection = styled.section`
  column-fill: auto;
  column-width: ${(props) => props.columnWidth}px;
  height: ${(props) => props.height}px;
`;

interface Props {
  spines: EpubSpineItem[];
  viewerSpines: string[];
}

const Viewer: React.FunctionComponent<Props> = ({ spines, viewerSpines }) => {
  const [viewerWidth, setViewerWidth] = useState(0);
  const [viewerHeight, setViewerHeight] = useState(0);

  useEffect(() => {
    console.log(window.innerWidth, window.innerHeight);
    setViewerWidth(window.innerWidth);
    setViewerHeight(window.innerHeight);
  }, []);

  const clickLeft = () => {
    window.scrollTo(629, 0);
  };

  return (
    <div>
      <div onClick={clickLeft}>
        MOVE
      </div>
      <ViewArticle>
        <ViewSection columnWidth={viewerWidth} height={viewerHeight} dangerouslySetInnerHTML={{ __html: viewerSpines[1] }} />
      </ViewArticle>
    </div>
  );
};

export default Viewer;
