/* eslint-disable react/no-danger */
import React, {
  useEffect, useState, useRef, useCallback,
} from 'react';

import styled from 'styled-components';

import { EpubSpineItem } from '../../interfaces/books';

const HiddenViewSection = styled.section`
  visibility: hidden;
`;

const ViewSection = styled.section`
  height: 100%;
  column-fill: auto;
  column-gap: 46px;
  column-width: 600px;
`;


const ViewArticle = styled.article`
  box-sizing: border-box;
  visibility: visible;
  width: 600px;
  height: ${(props) => props.styleProps.height}px;
  vertical-align: top;
  white-space: initial;
  display: inline-block;
  font-size: 1em !important;
  line-height: 1.67em !important;
  font-family: kopup_dotum !important;
  margin: 50px 139px;
  // overflow: hidden;
`;

const Contents = styled.div`
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
`;

interface Props {
  spines: EpubSpineItem[];
  viewerSpines: string[];
}

const Viewer: React.FunctionComponent<Props> = ({ spines, viewerSpines }) => {
  const [viewerWidth, setViewerWidth] = useState(0);
  const [viewerHeight, setViewerHeight] = useState(0);

  const [columnViewerCount, setColumnViewerCount] = useState(0);

  const viewSectionRef = useRef(null);
  const hiddenViewSectionRef = useRef(null);
  const contentRef = useRef(null);

  useEffect(() => {
    setViewerWidth(window.innerWidth * 0.7);
    setViewerHeight(window.innerHeight * 0.9);
  }, []);

  const clickRight = useCallback(() => {
    if (viewSectionRef) {
      const { current: hiddenViewSectionCurrent } = hiddenViewSectionRef;
      const count = hiddenViewSectionCurrent.clientHeight / viewerHeight + 1;
      console.log('BBBB', Math.floor(count));
      setColumnViewerCount(Math.floor(count));
      // window.scrollTo(600, 0);
    }
  }, [viewSectionRef, viewerHeight]);

  return (
    <ViewArticle
      onClick={clickRight}
      styleProps={{
        width: viewerWidth,
        height: viewerHeight,
      }}
    >
      <ViewSection
        ref={viewSectionRef}
        styleProps={{
          width: viewerWidth,
          height: viewerHeight,
        }}
      >
        <Contents ref={contentRef} dangerouslySetInnerHTML={{ __html: viewerSpines[1] }} />
      </ViewSection>
      <HiddenViewSection
        ref={hiddenViewSectionRef}
      >
        <Contents ref={contentRef} dangerouslySetInnerHTML={{ __html: viewerSpines[1] }} />
      </HiddenViewSection>
    </ViewArticle>
  );
};

export default Viewer;
