import React, { useCallback, useState } from 'react';

import styled from 'styled-components';

import ViewerSettingItem from './ViewerSettingItem';
import ViewerSettingColor from './ViewerSettingColor';

import * as settingActions from '../../reducers/viewerSetting';

import { subColor, defaultColor } from '../../styles';

import { SETTING_ITEM_TYPE, SETTING_ITEMS } from '../../constants/viewer';

const Container = styled.div`
  position: relative;
`;

const ToggleButton = styled.div`
  cursor: pointer;
`;

const SettingItems = styled.ul`
  position: absolute;
  width: 10em;
  left: 50%;
  transform: translateX(-50%);
  overflow: scroll;
  list-style-type: none;
  margin: 0;
  padding: 0;
  border: 1px solid ${subColor};
  background-color: ${defaultColor};
`;

const ViewerSetting: React.FunctionComponent = () => {
  const [isShowSetting, setIsShowSetting] = useState(true);

  const toggleShowNcs = useCallback(() => {
    setIsShowSetting(!isShowSetting);
  }, [isShowSetting]);

  const renderSettingItem = useCallback((settingItem) => {
    const { key, type, label } = settingItem;

    if (type === SETTING_ITEM_TYPE.COLOR) {
      return (
        <ViewerSettingColor
          key={key}
          label={label}
          value={0}
        />
      );
    }
    return (
      <ViewerSettingItem
        key={key}
        label={label}
        value={0}
      />
    );
  }, []);

  return (
    <Container>
      <ToggleButton onClick={toggleShowNcs}>
        설정
      </ToggleButton>
      {
        isShowSetting
        && (
        <SettingItems>
          {
            SETTING_ITEMS.map((settingItem) => renderSettingItem(settingItem))
          }
        </SettingItems>
        )
      }
    </Container>
  );
};

export default ViewerSetting;
