import React, { useCallback, useState, useMemo } from 'react';
import { useDispatch } from 'react-redux';

import styled from 'styled-components';

import * as actions from '../../reducers/viewer';

import { VIEWER_SLIDER_LEN_RATIO } from '../../constants/viewer';

const Container = styled.div`
  width: 100%;
  margin: auto 5%;
  flex-direction: column;
  display: flex;
`;

const Input = styled.input`
  margin-left: ${(100 - VIEWER_SLIDER_LEN_RATIO) / 2}%;
  width: ${VIEWER_SLIDER_LEN_RATIO}%;
  cursor: grab;
`;

const Marker = styled.div`
  margin-left: ${(100 - VIEWER_SLIDER_LEN_RATIO) / 2}%;
`;

interface Props {
  maxValue: number;
}

const ViewrSlider: React.FunctionComponent<Props> = ({ maxValue }) => {
  const dispatch = useDispatch();
  const [value, setValue] = useState(0);

  const hasMaxValue = useMemo(() => !!maxValue, [maxValue]);

  const onChangeSlider = useCallback((e) => {
    setValue(e.target.value);
    dispatch(actions.setViewerPageCount(Number(e.target.value)));
  }, [dispatch]);

  return (
    <Container>
      {
        hasMaxValue
        && (
        <>
          <Marker>
            {`${value}/${maxValue}`}
          </Marker>
          <Input type="range" min="0" max={maxValue} value={value} onChange={onChangeSlider} />
        </>
        )
      }
    </Container>
  );
};

export default ViewrSlider;
