import React, { useCallback, useState } from 'react';

import styled from 'styled-components';

import { subColor } from '../../styles';

import { EpubBook } from '../../interfaces/books';

const Container = styled.ul``;

const CoverImage = styled.li`
  width: 10em;
  min-height: 13em;
  border: 1px solid ${subColor};
  display: inline-block;
  margin: 1em;
  vertical-align: bottom;
  cursor: pointer;
  & img {
    width: 100%;
  }
`;

interface Props {
  books: EpubBook[];
}

const BookList: React.FunctionComponent<Props> = ({ books }) => {
  const [dragged, setDragged] = useState(null);

  const dragStart = useCallback((e) => {
    setDragged(e.currentTarget);
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('text/html', dragged);
  }, [dragged]);

  const dragEnd = useCallback((e) => {

  }, []);

  const dragOver = useCallback((e) => {
    e.preventDefault();
  }, []);

  const renderBook = useCallback((book) => {
    let imageSrc = 'default-book.png';
    const { cover, publicPath } = book;

    if (cover) {
      imageSrc = `${publicPath}/${book.cover.href}`;
    }
    return (
      <CoverImage
        draggable
        key={publicPath}
        onDragEnd={dragEnd}
        onDragStart={dragStart}
      >
        <img src={imageSrc} alt="Cover" />
      </CoverImage>
    );
  }, [dragStart, dragEnd]);

  return (
    <Container onDragOver={dragOver}>
      {
        books.map((book) => renderBook(book))
      }
    </Container>
  );
};

export default BookList;
