import React, { useCallback, useState, useEffect } from 'react';

interface Props {
  nowValue: number;
  maxValue: number;
}

const ViewrSlider: React.FunctionComponent<Props> = ({ nowValue = 0, maxValue }) => {
  const [value, setValue] = useState(0);

  useEffect(() => {
    setValue(nowValue);
  }, [nowValue]);

  const onChangeSlider = useCallback((e) => {
    setValue(e.target.value);
  }, []);

  return (
    <>
    <div>
      {`Now value: ${value}`}
      {`Max value: ${maxValue}`}
    </div>
      {
      maxValue
      && <input type="range" min="0" max={maxValue} value={value} onChange={onChangeSlider} />
    }
    </>
  );
};

export default ViewrSlider;
