import React, { useCallback } from 'react';

import styled from 'styled-components';

import { subColor } from '../../styles';

import { EpubBook } from '../../interfaces/books';

const CoverImage = styled.div`
  width: 10em;
  min-height: 12em;
  border: 1px solid ${subColor};
  display: inline-block;
  margin: 1em;
  & img {
    width: 100%;
  }
`;

interface Props {
  books: EpubBook[];
}

const BookList: React.FunctionComponent<Props> = ({ books }) => {
  const renderBook = useCallback((book) => {
    const { cover, publicPath } = book;

    if (cover) {
      return (
        <CoverImage key={publicPath}>
          <img src={`${publicPath}/${book.cover.href}`} alt="Cover" />
        </CoverImage>
      );
    }
    return (
      <CoverImage key={publicPath}>
        <img src='default-book.png' alt="Cover" />
      </CoverImage>
    )
  }, []);

  return (
    <>
      {
      books.map((book) => renderBook(book))
    }
    </>
  );
};

export default BookList;
