import React, { useEffect, useState } from 'react';

import styled from 'styled-components';

import { EpubSpineItem } from '../../interfaces/books';

const ViewArticle = styled.article`
  box-sizing: border-box;
  visibility: visible;
  width: 629px;
  height: ${(props) => props.styleProps.height}px;
  vertical-align: top;
  white-space: initial;
  display: inline-block;
  font-size: 1em !important;
  line-height: 1.67em !important;
  font-family: kopup_dotum !important;
  margin: 50px 139px;
`;

const ViewSection = styled.section`
  column-fill: auto;
  column-width: ${(props) => props.styleProps.width}px;
  height: ${(props) => props.styleProps.height}px;
`;

interface Props {
  spines: EpubSpineItem[];
  viewerSpines: string[];
}

const Viewer: React.FunctionComponent<Props> = ({ spines, viewerSpines }) => {
  const [wholeWidth, setWholeWidth] = useState(0);
  const [viewerWidth, setViewerWidth] = useState(0);
  const [viewerHeight, setViewerHeight] = useState(0);

  useEffect(() => {
    console.log(window.innerWidth, window.innerHeight);
    setViewerWidth(window.innerWidth);
    setViewerHeight(window.innerHeight);
  }, []);

  useEffect(() => {
    if (viewerWidth) {
      console.log('Outer', window.outerWidth);
      console.log('viewerWidth', viewerWidth);
      setWholeWidth(window.outerWidth);
    }
  }, [viewerWidth]);

  const clickLeft = () => {
    console.log("AAAA");
    window.scrollTo(viewerWidth, 0);
  };

  return (
    <ViewArticle
      onClick={clickLeft}
      styleProps={{
        height: viewerHeight,
      }}
    >
      <ViewSection
        styleProps={{
          width: viewerWidth,
          height: viewerHeight,
        }}
        dangerouslySetInnerHTML={{ __html: viewerSpines[1] }}
      />
    </ViewArticle>

  );
};

export default Viewer;
