import React from 'react';
import { NextPageContext } from 'next';
import styled from 'styled-components';

const Container = styled.div`
`;

const Home = (bookInfo) => {
  console.log('RRR', bookInfo);
  const { items, book } = bookInfo;

  const renderItems = (): JSX.Element => items.map((item, idx) => {
    const { mediaType } = book.items[idx];
    if (mediaType === 'image/jpeg') {
      return <img src={`data:image/jpeg;base64,${item.toString('base64')}`} alt="test" key={idx} />;
    }
    if (mediaType === 'text/css') {
      return <style key={idx}>{item}</style>;
    }
    if (mediaType === 'application/xhtml+xml') {
      // eslint-disable-next-line react/no-danger
      return <div dangerouslySetInnerHTML={{ __html: item }} key={idx} />;
    }
    return <div key={idx}>TEST</div>;
  });

  return (
    <Container>
      {renderItems()}
    </Container>
  );
};

Home.getInitialProps = async (context: NextPageContext): Promise<any> => {
  const { req } = context;
  if (req) {
    const { EpubParser } = require('@ridi/epub-parser');
    try {
      const parser = new EpubParser('public/pg61625-images.epub');
      const book = await parser.parse({
        parseStyle: false,
      });
      const spines = await parser.readItems(book.spines);
      const items = await parser.readItems(book.items, {
        force: true,
        extractBody: true,
        serializedAnchor: true,
        // basePath: 'http://www.gutenberg.org/files/61625/61625-h/images',
      });

      return {
        book,
        spines,
        items,
      };
    } catch (error) {
      console.log('Error', error);
    }
  }


  return {};
};

export default Home;
