import React, { useEffect, useState } from 'react';

import styled from 'styled-components';

import { EpubSpineItem } from '../../interfaces/books';

const ViewArticle = styled.article`
  box-sizing: border-box;
  visibility: visible;
  width: ${(props) => props.styleProps.width}px;
  height: ${(props) => props.styleProps.height}px;
  vertical-align: top;
  white-space: initial;
  display: inline-block;
  font-size: 1em !important;
  line-height: 1.67em !important;
  font-family: kopup_dotum !important;
  margin: 50px 139px;
`;

const ViewSection = styled.section`
  column-fill: auto;
  column-width: ${(props) => props.styleProps.width}px;
  height: ${(props) => props.styleProps.height}px;
`;

interface Props {
  spines: EpubSpineItem[];
  viewerSpines: string[];
}

const Viewer: React.FunctionComponent<Props> = ({ spines, viewerSpines }) => {
  const [viewerWidth, setViewerWidth] = useState(0);
  const [viewerHeight, setViewerHeight] = useState(0);

  const [isClickRight, setIsClickRight] = useState(false);

  useEffect(() => {
    console.log(window.innerWidth, window.innerHeight);
    setViewerWidth(window.innerWidth * 0.7);
    setViewerHeight(window.innerHeight * 0.8);
  }, []);

  const clickRight = () => {
    console.log("AAAA");
    setIsClickRight(true);
    window.scrollTo(viewerWidth, 0);
    setIsClickRight(false);
  };

  return (
    <ViewArticle
      onClick={clickRight}
      style={{
        overflow: isClickRight ? 'hidden' : '',
      }}
      styleProps={{
        width: viewerWidth,
        height: viewerHeight,
      }}
    >
      <ViewSection
        styleProps={{
          width: viewerWidth,
          height: viewerHeight,
        }}
        dangerouslySetInnerHTML={{ __html: viewerSpines[1] }}
      />
    </ViewArticle>

  );
};

export default Viewer;
