import React, { useRef, useEffect } from 'react';
import { useDispatch } from 'react-redux';

import {
  HiddenViewerContainer, HiddenViewSection,
  Contents,
} from '../../styles/viewer';

import * as actions from '../../reducers/viewer';

interface Props {
  viewerWidth: number;
  viewerSpineIndex: number;
  viewerSpine: string;
}

const ViewerCount: React.FunctionComponent<Props> = ({
  viewerWidth,
  viewerSpineIndex, viewerSpine,
}) => {
  const dispatch = useDispatch();
  const hiddenViewContainerRef = useRef(null);
  const hiddenViewSectionRef = useRef(null);

  useEffect(() => {
    const { current: hiddenViewContainerCurrent } = hiddenViewContainerRef;
    const count = hiddenViewContainerCurrent.scrollWidth / viewerWidth;

    dispatch(actions.setViewerCount({
      index: viewerSpineIndex,
      count: Math.floor(count),
    }));
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <HiddenViewerContainer ref={hiddenViewContainerRef}>
      <HiddenViewSection ref={hiddenViewSectionRef}>
        <Contents dangerouslySetInnerHTML={{ __html: viewerSpine }} />
      </HiddenViewSection>
    </HiddenViewerContainer>

  );
};

export default ViewerCount;
