import React, { useState, useCallback } from 'react';

import styled from 'styled-components';

import { EpubNcxItem } from '../../interfaces/books';

const Container = styled.div`
  position: relative;
`;

const ToggleButton = styled.div`
  cursor: pointer;
`;

const NavPointItems = styled.div`
  position: absolute;
  width: 10em;
  left: 50%;
  transform: translateX(-50%);
`;

const NavPointItem = styled.div`
  text-align: center;
`;

interface Props {
  ncxItem?: EpubNcxItem;
}

const ViewerNcx: React.FunctionComponent<Props> = ({ ncxItem }) => {
  const { navPoints } = ncxItem;

  const [isShowNcx, setIsShowNcx] = useState(false);

  const toggleShowNcs = useCallback(() => {
    setIsShowNcx(!isShowNcx);
  }, [isShowNcx]);

  return (
    <Container>
      <ToggleButton onClick={toggleShowNcs}>
        목차
      </ToggleButton>
      <NavPointItems>
        {
          isShowNcx && navPoints.map((navPoint) => <NavPointItem key={navPoint.label}>{navPoint.label}</NavPointItem>)
        }
      </NavPointItems>
    </Container>
  );
};

export default ViewerNcx;
