import React from 'react';
import { NextPageContext } from 'next';
import styled from 'styled-components';

const Test = styled.div`
`;

const Home = (test) => {
  console.log('RRR', test);
  const { spines, items, book } = test;

  const renderItems = () => {
    items.map((item, idx) => {
      const { mediaType } = book.items[idx];
      if (mediaType === 'image/jpeg') {
        return <img src={`data:image/jpeg;base64,${item.toString('base64')}`} />;
      } if (mediaType === 'text/css') {

      }

      return <div dangerouslySetInnerHTML={{ __html: item }} key={idx} />;
    });
  };

  return (
    <Test>
      {
        items.map((spine, idx) => (
          <>
            {
          (idx === 0 || idx === 1)
          // eslint-disable-next-line react/no-danger
          // <div dangerouslySetInnerHTML={{ __html: spine }} key={idx} />
          && <img src={`data:image/jpeg;base64,${spine.toString('base64')}`} />
          }
          </>

        ))
      }
    </Test>
  );
};

Home.getInitialProps = async (context: NextPageContext): Promise<any> => {
  const { req } = context;
  if (req) {
    // eslint-disable-next-line @typescript-eslint/no-var-requires
    const { EpubParser } = require('@ridi/epub-parser');
    try {
      const parser = new EpubParser('public/pg61625-images.epub');
      const book = await parser.parse({
        parseStyle: false,
      });
      const spines = await parser.readItems(book.spines);
      const items = await parser.readItems(book.items, {
        force: true,
        extractBody: true,
        serializedAnchor: true,
      });

      return {
        book,
        spines,
        items,
      };
    } catch (error) {
      console.log('Error', error);
    }
  }


  return {};
};

export default Home;
