import React, { useEffect } from 'react';

import styled from 'styled-components';

import { EpubSpineItem } from '../../interfaces/books';

const ViewSection = styled.section`
  column-width: ${(props) => props.width}px;
  height: ${(props) => props.height}px;
`;

interface Props {
  spines: EpubSpineItem[];
  viewerSpines: string[];
}

const Viewer: React.FunctionComponent<Props> = ({ spines, viewerSpines }) => {
  return (
    <>
      {
        <ViewSection width={window.innerWidth} height={window.innerHeight} dangerouslySetInnerHTML={{ __html: viewerSpines[1] }} />
        //  eslint-disable-next-line react/no-danger
        // spines.map((spine, idx) => <ViewSection width={window.innerWidth} dangerouslySetInnerHTML={{ __html: viewerSpines[idx] }} />)
      }
    </>
  );
};

export default Viewer;
