import React, { useEffect, useState } from 'react';
import { NextPageContext, NextPage } from 'next';

import styled from 'styled-components';

import Viewer from '../components/viewer/Viewer';

import { EpubBook } from '../interfaces/books';

interface Props {
  book: EpubBook;
  readSpines: string[];
  basePath: string;
}

const Container = styled.div`
`;

const Home: NextPage<Props> = (bookInfo) => {
  const [styleTags, setStyleTags] = useState([]);

  const {
    readSpines, book, basePath,
  } = bookInfo;

  useEffect(() => {
    const {
      styles,
    } = book;

    setStyleTags(styles.map((style) => <link rel="stylesheet" href={`${basePath}/${style.href}`} />));
  }, [basePath, book]);

  return (
    <Viewer
      styleTags={styleTags}
      spines={readSpines}
    />
  );
};

// eslint-disable-next-line @typescript-eslint/unbound-method
Home.getInitialProps = async (context: NextPageContext<any>): Promise<any> => {
  const { req } = context;
  if (req) {
    const { EpubParser } = require('@ridi/epub-parser');
    try {
      const parser = new EpubParser('public/pg61625-images.epub');
      const book: EpubBook = await parser.parse({
        validatePackage: true,
        parseStyle: false,
        unzipPath: 'public/epub/pg61625-images',
      });
      const readSpines = await parser.readItems(book.spines, {
        force: true,
        extractBody: true,
        serializedAnchor: true,
        ignoreScript: true,
        basePath: 'epub/pg61625-images',
      });

      return {
        book,
        readSpines,
        basePath: 'epub/pg61625-images',
      };
    } catch (error) {
      console.log('Error', error);
    }
  }


  return {};
};

export default Home;
