import React from 'react';

import styled from 'styled-components';

import { subColor } from '../../styles';
import { ViewerSettingItemStyle } from '../../styles/viewer';

const Container = styled.div`
  ${ViewerSettingItemStyle}
`;

const Label = styled.div`
  margin: auto .5em auto 0;
`;

const Value = styled.div`
  margin: auto auto auto 0;
`;

const Controller = styled.div`
  display: flex;
  width: 35%;
  margin: auto 0 auto auto;
  border: 1px solid ${subColor};
  border-radius: 1em;
  padding-left: .3em;
  padding-right: .3em;
`;

const PlusButton = styled.div`
  width: 50%;
  cursor: pointer;
  border-right: 1px solid ${subColor};
  display: flex;
  & div {
    margin: auto;
  }
`;

const MinusButton = styled.div`
  width: 50%;
  cursor: pointer;
`;

interface Props {
  label: string;
  value: number;
}

const ViewerSettingItem: React.FunctionComponent<Props> = ({ label, value }) => (
  <Container>
    <Label>
      {label}
    </Label>
    <Value>
      {value}
    </Value>
    <Controller>
      <PlusButton>
        <div>
          +
        </div>
      </PlusButton>
      <MinusButton>
        <div>
          -
        </div>
      </MinusButton>
    </Controller>
  </Container>
);

export default ViewerSettingItem;
