import React from 'react';
import { useSelector } from 'react-redux';
import { NextPageContext, NextPage } from 'next';

import Layout from '../components/Layout';
import BookList from '../components/books/BookList';

import * as booksActions from '../reducers/books';

import { getBook, getViewers } from '../lib/util';

import { ReducerState } from '../interfaces';
import { BookInfo, EpubBook } from '../interfaces/books';

const Home: NextPage = () => {
  const { list } = useSelector((state: ReducerState) => state.books);

  return (
    <Layout>
      <BookList books={list} />
    </Layout>
  );
};
// eslint-disable-next-line @typescript-eslint/unbound-method
Home.getInitialProps = async (context: NextPageContext<any>): Promise<any> => {
  const { req, store } = context;
  if (req) {
    const fs = require('fs');
    const { EpubParser } = require('@ridi/epub-parser');
    const books: BookInfo[] = [];

    const files = fs.readdirSync('public/');
    try {
      // eslint-disable-next-line no-restricted-syntax
      for (const file of files) {
        if (file.includes('.epub')) {
          const publicPath = `epub/${file.split('.epub')[0]}`;

          const parser = new EpubParser(`public/${file}`);
          const book: EpubBook = await getBook(parser, {
            unzipPath: `public/${publicPath}`,
          });

          if (book) {
            const viewers = await getViewers(parser, {
              bookSpines: book.spines,
              publicPath,
            });
            books.push({
              book,
              viewers,
              publicPath,
            });
          }
        }
      }
      store.dispatch(booksActions.setBookList(books));

      return {
        books,
      };
    } catch (error) {
      console.log('Error', error);
    }
  }
  return {};
};

export default Home;
