import React, { useCallback } from 'react';

import { EpubBook } from '../../interfaces/books';

interface Props {
  books: EpubBook[];
}

const BookList: React.FunctionComponent<Props> = ({ books }) => {
  const renderBook = useCallback((book) => {
    const { cover, publicPath } = book;

    if (cover) {
      return <img src={`${publicPath}/${book.cover.href}`} alt="Cover" />;
    }
    return <div>NONE</div>;
  }, []);

  return (
    <>
      {
      books.map((book) => renderBook(book))
    }
    </>
  );
};

export default BookList;
