import React, { useCallback, useState, useEffect } from 'react';

import styled from 'styled-components';
import { subColor } from '../../styles';

const Container = styled.div`
  position: absolute;
  width: 100%;
  bottom: 0;
  border-top: 1px solid ${subColor};
  & input {
    margin-left: 5%;
    width: 90%;
    cursor: grab;
  }
`;

const Marker = styled.div`
  margin-left: 5%;
`;

interface Props {
  nowValue: number;
  maxValue: number;
}

const ViewrSlider: React.FunctionComponent<Props> = ({ nowValue = 0, maxValue }) => {
  const [value, setValue] = useState(0);

  useEffect(() => {
    setValue(nowValue);
  }, [nowValue]);

  const onChangeSlider = useCallback((e) => {
    setValue(e.target.value);
  }, []);

  return (
    <Container>
      <Marker>
        {`${value}/${maxValue}`}
      </Marker>
      {
        maxValue
        && <input type="range" min="0" max={maxValue} value={value} onChange={onChangeSlider} />
      }
    </Container>
  );
};

export default ViewrSlider;
