import React from 'react';
import { NextPageContext } from 'next';
import styled from 'styled-components';

const Test = styled.div`
  background-color: black;
  color: white;
`;

const Home = ({ spines, description }) => {
  console.log('RRR', spines, description);

  return (
    <Test>
      Home
    </Test>
  );
};

Home.getInitialProps = async (context: NextPageContext): Promise<any> => {
  const { req } = context;
  if (req) {
    // eslint-disable-next-line @typescript-eslint/no-var-requires
    const { EpubParser } = require('@ridi/epub-parser');
    console.log('Cddlled');
    try {
      const parser = new EpubParser('public/pg61611-images.epub');
      const book = await parser.parse();
      const spines = await parser.readItems(book.spines);
      const description = await parser.readItems(book.description);

      return {
        spines,
        description,
      };
    } catch (error) {
      console.log('Error', error);
    }
  }


  return {};
};

export default Home;
