import React, { useEffect, useState, useRef } from 'react';

import styled from 'styled-components';

import { EpubSpineItem } from '../../interfaces/books';

const ViewArticle = styled.article`
  box-sizing: border-box;
  visibility: visible;
  vertical-align: top;
  white-space: initial;
  display: inline-block;
  font-size: 1em !important;
  line-height: 1.67em !important;
`;

const ViewSection = styled.section`
  column-fill: auto;
  column-width: ${(props) => props.styleProps.width}px;
  height: ${(props) => props.styleProps.height}px;
`;

interface Props {
  spines: EpubSpineItem[];
  viewerSpines: string[];
}

const Viewer: React.FunctionComponent<Props> = ({ spines, viewerSpines }) => {
  const [viewerWidth, setViewerWidth] = useState(0);
  const [viewerHeight, setViewerHeight] = useState(0);
  const viewerRef = useRef();

  useEffect(() => {
    console.log(window.innerWidth, window.innerHeight);
    setViewerWidth(window.innerWidth);
    setViewerHeight(window.innerHeight);
  }, []);

  useEffect(() => {
    const { current: viewerRefCurrent } = viewerRef;
    console.log('viewerRefCurrent', viewerRefCurrent);
  }, [viewerRef]);

  const clickLeft = () => {
    window.scrollTo(629, 0);
  };

  return (
    <div>
      <ViewArticle>
        <ViewSection
          ref={viewerRef}
          styleProps={{
            width: viewerWidth,
            height: viewerHeight,
          }}
          dangerouslySetInnerHTML={{ __html: viewerSpines[1] }}
        />
      </ViewArticle>
    </div>
  );
};

export default Viewer;
