import React from 'react';
import { NextPageContext } from 'next';
import styled from 'styled-components';

const Test = styled.div`
  background-color: black;
  color: white;
`;

const Home = (test) => {
  console.log('RRR', test);
  const { spines } = test;

  return (
    <Test>
      {
        spines.map((spine) => {
          return (
            <div dangerouslySetInnerHTML={{_html: spines}}>
            </div>
          )
        })
      }
    </Test>
  );
};

Home.getInitialProps = async (context: NextPageContext): Promise<any> => {
  const { req } = context;
  if (req) {
    // eslint-disable-next-line @typescript-eslint/no-var-requires
    const { EpubParser } = require('@ridi/epub-parser');
    try {
      const parser = new EpubParser('public/pg61611-images.epub');
      const book = await parser.parse();
      const spines = await parser.readItems(book.spines);

      return {
        titles: book.titles,
        spines,
        images: book.images,
        items: book.items,
        metas: book.metas
      };
    } catch (error) {
      console.log('Error', error);
    }
  }


  return {};
};

export default Home;
