import { EpubParser } from '@ridi/epub-parser';

class Parser {
  _epub: any;

  constructor(epubUrl: string) {
    this._epub = new EpubParser(epubUrl);
  }

  async parse(): Promise<any> {
    try {
      const book = await this._epub.parse();
      const result = await this._epub.readItems(book.spines);

      return result;
    } catch (error) {
      throw new Error(`Parse error. ${error}`);
    }
  }
}

export default Parser;
