import React, { useRef, useEffect } from 'react';

import { HiddenViewSection, Contents } from '../../styles/viewer';

import { VIEWER_HEIGHT_RATIO } from '../../constants/viewer';

interface Props {
  viewerSpineIndex: number;
  viewerSpine: string;
  viewerColumnCountCallback: (spineIndex: number, count: number) => void;
}

const ViewerCount: React.FunctionComponent<Props> = ({
  viewerSpineIndex, viewerSpine,
  viewerColumnCountCallback,
}) => {
  const hiddenViewSectionRef = useRef(null);

  useEffect(() => {
    if (hiddenViewSectionRef) {
      const viewerHeight = Math.floor(window.innerHeight * (VIEWER_HEIGHT_RATIO / 100));
      const { current: hiddenViewSectionCurrent } = hiddenViewSectionRef;
      const count = hiddenViewSectionCurrent.clientHeight / viewerHeight;

      viewerColumnCountCallback(viewerSpineIndex, Math.floor(count));
    }
  }, []);

  return (
    <HiddenViewSection
      ref={hiddenViewSectionRef}
    >
      <Contents dangerouslySetInnerHTML={{ __html: viewerSpine }} />
    </HiddenViewSection>
  );
};

export default ViewerCount;
