import React, { useRef, useEffect } from 'react';
import { useDispatch } from 'react-redux';

import { HiddenViewSection, Contents, ViewArticle } from '../../styles/viewer';

import { VIEWER_HEIGHT_RATIO } from '../../constants/viewer';

import * as actions from '../../reducers/viewer';

interface Props {
  viewerSpineIndex: number;
  viewerSpine: string;
}

const ViewerCount: React.FunctionComponent<Props> = ({
  viewerSpineIndex, viewerSpine,
}) => {
  const dispatch = useDispatch();
  const hiddenViewSectionRef = useRef(null);

  useEffect(() => {
    if (hiddenViewSectionRef) {
      const viewerHeight = Math.floor(window.innerHeight * (VIEWER_HEIGHT_RATIO / 100));
      const { current: hiddenViewSectionCurrent } = hiddenViewSectionRef;
      const count = hiddenViewSectionCurrent.clientHeight / viewerHeight;

      console.log('hiddenViewSectionCurrent.clientHeight', hiddenViewSectionCurrent.clientHeight, viewerHeight);

      dispatch(actions.setViewerCount({
        index: viewerSpineIndex,
        count: count,
      }));
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <ViewArticle styleProps={{width: 600, height: 1008}}>
    <HiddenViewSection
      ref={hiddenViewSectionRef}
    >
      <Contents dangerouslySetInnerHTML={{ __html: viewerSpine }} />
    </HiddenViewSection>
    </ViewArticle>

  );
};

export default ViewerCount;
