import React from 'react';

import styled from 'styled-components';

import ViewerNcx from './ViewerNcx';

import { EpubNcxItem } from '../../interfaces/books';

const Container = styled.div`
  width: 100%;
  height: ${(100 - VIEWER_HEIGHT_RATIO) / 2}%
  position: absolute;
  display: flex;
`;

const Titles = styled.div`
  white-space: nowrap;
  vertical-align: top;
  & div {
    display: inline-block;
  }
`;

const NavPoints = styled.div`
  margin-left: auto;
`;

interface Props {
  titles: string[];
  ncxItem: EpubNcxItem;
}

const ViewerHeader: React.FunctionComponent<Props> = ({ titles, ncxItem }) => (
  <Container>
    <Titles>
      {
          titles.map((title) => (
            <div key={title}>{title}</div>
          ))
        }
    </Titles>
    <NavPoints>
      <ViewerNcx ncxItem={ncxItem} />
    </NavPoints>
  </Container>
);

export default ViewerHeader;
