import React, { useRef, useEffect } from 'react';
import { useDispatch } from 'react-redux';

import {
  HiddenViewerContainer, HiddenViewSection,
  Contents,
} from '../../styles/viewer';

import { VIEWER_HEIGHT_RATIO } from '../../constants/viewer';

import * as actions from '../../reducers/viewer';

interface Props {
  viewerSpineIndex: number;
  viewerSpine: string;
}

const ViewerCount: React.FunctionComponent<Props> = ({
  viewerSpineIndex, viewerSpine,
}) => {
  const dispatch = useDispatch();
  const hiddenViewContainerRef = useRef(null);
  const hiddenViewSectionRef = useRef(null);

  useEffect(() => {
    const viewerHeight = Math.floor(window.innerHeight * (VIEWER_HEIGHT_RATIO / 100));

    const { current: hiddenViewContainerCurrent } = hiddenViewContainerRef;
    const { current: hiddenViewSectionCurrent } = hiddenViewSectionRef;

    const count = hiddenViewSectionCurrent.clientHeight / viewerHeight;
    console.log(hiddenViewContainerCurrent.scrollWidth / 782);
    console.log(hiddenViewSectionCurrent.offsetHeight);
    console.log(viewerHeight, count);

    dispatch(actions.setViewerCount({
      index: viewerSpineIndex,
      count: Math.floor(count),
    }));
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <HiddenViewerContainer ref={hiddenViewContainerRef}>
      <HiddenViewSection ref={hiddenViewSectionRef}>
        <Contents dangerouslySetInnerHTML={{ __html: viewerSpine }} />
      </HiddenViewSection>
    </HiddenViewerContainer>

  );
};

export default ViewerCount;
