import React, { useCallback, useState } from 'react';

import styled from 'styled-components';

import { subColor } from '../../styles';

import { EpubBook } from '../../interfaces/books';

const Container = styled.ul``;

const CoverImage = styled.li`
  width: 10em;
  min-height: 13em;
  border: 1px solid ${subColor};
  display: inline-block;
  margin: 1em;
  vertical-align: bottom;
  & img {
    width: 100%;
  }
`;

const DEFAULT_IMAGE = 'default-book.png';

interface Props {
  books: EpubBook[];
}


const BookList: React.FunctionComponent<Props> = ({ books }) => {
  const [draggedItem, setDraggedItem] = useState<EpubBook>(null);

  const dragStart = useCallback((e, index) => {
    console.log('Drag start');
    setDraggedItem(books[index]);
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('text/html', e.target.parentNode);
    e.dataTransfer.setDragImage(e.target.parentNode, 20, 20);
  }, [books]);

  const dragOver = useCallback((index) => {
    const draggedOverItem = books[index];
    console.log("AAA", draggedItem);
    // // if the item is dragged over itself, ignore
    // if (draggedItem === draggedOverItem) {
    //   return;
    // }

    // // filter out the currently dragged item
    // let items = books.filter(item => item !== draggedItem);

    // // add the dragged item after the dragged over item
    // items.splice(index, 0, draggedItem);
  }, [books, draggedItem]);

  const dragEnd = useCallback((e) => {
    e.preventDefault();
  }, []);

  return (
    <Container>
      {
        books.map(({ cover, publicPath }, index) => (
          <CoverImage
            onDragOver={() => dragOver(index)}
            key={publicPath}
          >
            <img
              src={cover ? `${publicPath}/${cover.href}` : DEFAULT_IMAGE}
              draggable
              onDragStart={(e) => dragStart(e, index)}
              onDragEnd={dragEnd}
              alt="Cover"
            />
          </CoverImage>
        ))
      }
    </Container>
  );
};

export default BookList;
