/* eslint-disable react/no-danger */
import React, {
  useEffect, useState, useRef, useCallback,
} from 'react';

import styled from 'styled-components';

import { EpubSpineItem } from '../../interfaces/books';

const ViewrPage = styled.section`
  min-height: 100vh;
  padding-bottom: 0;
  // overflow: auto;
`;

const ViewerContents = styled.div`
  box-sizing: border-box;
  height: 1008px;
  overflow: hidden;
`;

const ViewArticle = styled.article`
  box-sizing: border-box;
  visibility: visible;
  width: ${(props) => props.styleProps.width}px;
  height: ${(props) => props.styleProps.height}px;
  vertical-align: top;
  white-space: initial;
  display: inline-block;
  font-size: 1em !important;
  margin: 50px 139px;
`;

const ViewSection = styled.section`
  column-fill: auto;
  column-width: ${(props) => props.styleProps.width}px;
  height: 100%;
`;

const TestDiv = styled.div`
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
`;

interface Props {
  spines: EpubSpineItem[];
  viewerSpines: string[];
}

const Viewer: React.FunctionComponent<Props> = ({ spines, viewerSpines }) => {
  const [viewerWidth, setViewerWidth] = useState(0);
  const [viewerHeight, setViewerHeight] = useState(0);

  const contentRef = useRef(null);
  const viewRef = useRef(null);

  useEffect(() => {
    setViewerWidth(window.innerWidth * 0.7);
    setViewerHeight(window.innerHeight * 0.9);
  }, []);

  const clickRight = useCallback(() => {
    if (viewRef && contentRef) {
      const { current: contentRefCurrent } = contentRef;
      const { current: viewRefCurrent } = viewRef;

      console.log('AAAA', contentRefCurrent.offsetWidth);
      console.log(viewRefCurrent.offsetWidth);
      window.scrollTo(600, 0);
      // viewRef.current.scrollTo(50, 0);
    }
  }, [viewRef, contentRef]);

  return (
    <ViewrPage>
      <ViewerContents>
        <ViewArticle
          onClick={clickRight}
          styleProps={{
            width: viewerWidth,
            height: viewerHeight,
          }}
        >
          <ViewSection
            ref={viewRef}
            styleProps={{
              width: viewerWidth,
              height: viewerHeight,
            }}
          >
            <TestDiv ref={contentRef} dangerouslySetInnerHTML={{ __html: viewerSpines[1] }} />
          </ViewSection>
        </ViewArticle>
      </ViewerContents>
    </ViewrPage>


  );
};

export default Viewer;
