import React from 'react';

import styled from 'styled-components';

import { subColor } from '../../styles';
import {
  ViewerSettingItemStyle,
  ViewerSettingValueStyle,
  ViewerSettingLabelStyle,
} from '../../styles/viewer';

const Container = styled.div`
  ${ViewerSettingItemStyle}
`;

const Label = styled.div`
  ${ViewerSettingLabelStyle}
`;

const Value = styled.div`
  ${ViewerSettingValueStyle}
`;

const Controller = styled.div`
  display: flex;
  width: 30%;
  margin: auto 0 auto auto;
  border: 1px solid ${subColor};
  border-radius: 1em;
  padding-left: .3em;
  padding-right: .3em;
`;

const Content = styled.div`
  width: 1em;
  height: 1em;
  border: 1px solid ${subColor};  
  border-radius: 50%;
  background-color: ${(props) => props.styleProps.color};
`;

interface Props {
  label: string;
  value: number | string;
  colors: string[];
  action: (param: string | number) => void;
}

const ViewerSettingColorItem: React.FunctionComponent<Props> = ({
  label, value, colors, action,
}) => (
  <Container>
    <Label>
      {label}
    </Label>
    <Value>
      {value}
    </Value>
    <Controller>
      {
        colors.map((color) => {
          return (
            <Content
              styleProps={{
                color,
              }}
            />
          );
        })
      }
    </Controller>
  </Container>
);

export default ViewerSettingColorItem;
