import React, { useEffect } from 'react';

import styled from 'styled-components';

import { EpubSpineItem } from '../../interfaces/books';

const ViewSection = styled.section`
  column-width: ${props => props.width};
`;

interface Props {
  spines: EpubSpineItem[];
  viewerSpines: string[];
}

const Viewer: React.FunctionComponent<Props> = ({ spines, viewerSpines }) => {
  useEffect(() => {
    console.log(window.innerWidth);
  })
  return (
    <>
      {
        <ViewSection width={window.innerWidth} dangerouslySetInnerHTML={{ __html: viewerSpines[1] }} />
      }
    </>
  );
  // eslint-disable-next-line react/no-danger
  // spines.map((spine, idx) => <ViewSection dangerouslySetInnerHTML={{ __html: viewerSpines[idx] }} />)
};

export default Viewer;
