import React, { useState, useCallback } from 'react';

import styled from 'styled-components';

import { subColor } from '../../styles';

import { EpubNcxItem } from '../../interfaces/books';

const Container = styled.div`
  position: relative;
`;

const ToggleButton = styled.div`
  cursor: pointer;
`;

const NavPointItems = styled.div`
  position: absolute;
  width: 10em;
  height: 30em;
  left: 50%;
  transform: translateX(-50%);
  overflow: scroll;
  border-top: 1px solid ${subColor};
  border-left: 1px solid ${subColor};
  border-right: 1px solid ${subColor};
`;

const NavPointItem = styled.div`
  text-align: center;
  padding: 2px;
  border-bottom: 1px solid ${subColor};
  cursor: pointer;
`;

interface Props {
  ncxItem?: EpubNcxItem;
}

const ViewerNcx: React.FunctionComponent<Props> = ({ ncxItem }) => {
  const { navPoints } = ncxItem;

  const [isShowNcx, setIsShowNcx] = useState(false);

  const toggleShowNcs = useCallback(() => {
    setIsShowNcx(!isShowNcx);
  }, [isShowNcx]);

  return (
    <Container>
      <ToggleButton onClick={toggleShowNcs}>
        목차
      </ToggleButton>
      {
        isShowNcx
        && (
        <NavPointItems>
          {
            navPoints.map((navPoint) => <NavPointItem key={navPoint.label}>{navPoint.label}</NavPointItem>)
          }
        </NavPointItems>
        )
      }
    </Container>
  );
};

export default ViewerNcx;
