import React, { useEffect, useState } from 'react';
import { NextPageContext, NextPage } from 'next';


import ViewerPageComponent from '../components/viewer/ViewerPage';

import { EpubBook } from '../interfaces/books';
import Layout from '../components/Layout';

interface Props {
  book: EpubBook;
  viewerSpines: string[];
  styleTags: string[];
}

const Viewer: NextPage<Props> = ({ book, viewerSpines, styleTags }) => {
  const [flag, setFlag] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setFlag(true);
    }, 3000);
  }, []);

  console.log(styleTags);

  return (
    <Layout
      styleTags={styleTags}
    >

      {
        flag
        && (
        <ViewerPageComponent
          spines={book.spines}
          viewerSpines={viewerSpines}
        />
        )
      }

    </Layout>
  );
};

// eslint-disable-next-line @typescript-eslint/unbound-method
Viewer.getInitialProps = async (context: NextPageContext<any>): Promise<any> => {
  const { req } = context;
  if (req) {
    const { EpubParser } = require('@ridi/epub-parser');
    try {
      const parser = new EpubParser('public/jikji.epub');
      const book: EpubBook = await parser.parse({
        validatePackage: true,
        parseStyle: false,
        unzipPath: 'public/epub/jikji',
      });
      const viewerSpines = await parser.readItems(book.spines, {
        force: true,
        extractBody: true,
        serializedAnchor: true,
        ignoreScript: true,
        basePath: 'epub/jikji',
      });

      return {
        book,
        viewerSpines,
        styleTags: book.styles.map((style) => <link rel="stylesheet" href={`epub/jikji/${style.href}`} key={style.href} />),
      };
    } catch (error) {
      console.log('Error', error);
    }
  }


  return {};
};

export default Viewer;
