import React, { useCallback, useState } from 'react';

import styled from 'styled-components';

import { subColor } from '../../styles';

import { EpubBook } from '../../interfaces/books';

const Container = styled.ul``;

const CoverImage = styled.li`
  width: 10em;
  min-height: 13em;
  border: 1px solid ${subColor};
  display: inline-block;
  margin: 1em;
  vertical-align: bottom;
  & img {
    width: 100%;
  }
`;

const DEFAULT_IMAGE = 'default-book.png'; 

interface Props {
  books: EpubBook[];
}


const BookList: React.FunctionComponent<Props> = ({ books }) => {
  var placeholder = document && document.createElement("li");
  placeholder.className = "placeholder";
  const dragStart = useCallback((e) => {
    console.log('Drag start');
    this.dragged = e.currentTarget;
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('text/html', this.dragged);
  }, []);

  const dragEnd = useCallback((e) => {
    console.log('Drag end');
    this.dragged.style.display = 'block';
    this.dragged.parentNode.removeChild(placeholder); // update state
    var data = this.state.colors;
    var from = Number(this.dragged.dataset.id);
    var to = Number(this.over.dataset.id);
    if(from < to) to--;
    data.splice(to, 0, data.splice(from, 1)[0]);
    this.setState({colors: data});

  }, []);

  const dragOver = useCallback((e) => {
    e.preventDefault();
    this.dragged.style.display = "none";
    if(e.target.className === 'placeholder') return;
    this.over = e.target;
    e.target.parentNode.insertBefore(placeholder, e.target);

  }, []);

  return (
    <Container onDragOver={dragOver}>
      {
        books.map(({ cover, publicPath }) => {
          return (
            <CoverImage
              draggable
              key={publicPath}
              onDragEnd={dragEnd}
              onDragStart={dragStart}
            >
              <img src={cover ? `${publicPath}/${cover.href}` : DEFAULT_IMAGE} alt="Cover" />
            </CoverImage>
          );
        })
      }
    </Container>
  );
};

export default BookList;
